import React, { useState } from 'react';

export default function Auth() {

  return (
    <div className='Auth'>
      <Login />
      <Register />
    </div>
  )
}

const Login = () => {
  return (
    <Form label={'Login'} username={username} password={password} setUsername={setUsername} setPassword={setPassword} />
  )
}

const Register = () => {

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  return (
    <Form label={'Register'} username={username} password={password} setUsername={setUsername} setPassword={setPassword} />
  )
}

const Form = ({ label, username, setUsername, password, setPassword }) => {
  return (
    <div className='auth-container'>
      <h2>{label}</h2>
      <form>
        <div className='form-group'>
          <label htmlFor='username'>Username: </label>
          <input
            type='text'
            id='username'
            value={username}
            onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className='form-group'>
          <label htmlFor='password'>Password: </label>
          <input
            type='password'
            id='password'
            value={password}
            onChange={(e) => setPassword(e.target.password)} />
        </div>
        <button type='submit'>{label}</button>
      </form>
    </div>
  )
}
